mapper.update:
update T_CPIC_CODE_TYPE
ID=#{id}, TYPE=#{type}, CODE=#{code}, CONTENT=#{content}, REMARK=#{remark}, FST=#{fst}, LMT=#{lmt}, FOID=#{foid}, LOID=#{loid},
where id=#{id}

mapper.select:
select t.ID as id, t.TYPE as type, t.CODE as code, t.CONTENT as content, t.REMARK as remark, t.FST as fst, t.LMT as lmt, t.FOID as foid, t.LOID as loid from T_CPIC_CODE_TYPE t where 1=1
and t.content = #{content}
order by to_char(t.LMT, 'YYYY-MM-DD') desc

select t.ID as id,t.TYPE as type,t.CODE as code,t.CONTENT as content
from T_CPIC_CODE_TYPE t
where 1=1

    and t.type = #{type}


    and t.CODE = #{code}


    and t.CONTENT = #{content}
