mapper.select:
select *
from (SELECT *
FROM T_CPIC_INSUREDINTENTION S
WHERE s.expireDate is not null
and s.CALLNUMCONFIGID = #{callNumConfigId}
and s.branchName = #{branchName}

    and s.BUSINESSTYPE in

        #{businessTypes}


ORDER BY DECODE(SIGN(TO_CHAR(s.expireDate, 'mm') - TO_CHAR(sysdate, 'mm')),
-1,
TO_CHAR(s.expireDate, 'mm') + '12',
TO_CHAR(s.expireDate, 'mm')) - TO_CHAR(sysdate, 'mm'), TO_CHAR(s.expireDate, 'DD'))
where ROWNUM <= #{limit}

select CALLNUMBER
from T_CPIC_INSUREDINTENTION
where CALLNUMCONFIGID = #{callNumConfigId}
group by CALLNUMBER
having count(CALLNUMBER) >= #{repeatNum}

mapper.insert:
insert into T_CPIC_INSUREDINTENTION_HIS select * from T_CPIC_INSUREDINTENTION where CALLNUMBER in

    #{list}

and CALLNUMCONFIGID = #{callNumConfigId}

delete from T_CPIC_INSUREDINTENTION where CALLNUMBER in

    #{list}

and CALLNUMCONFIGID = #{callNumConfigId}
